mapper.select:
SELECT user_id, pw, user_role, enable, profile_image
FROM user_table
WHERE user_id = #{userId}

SELECT COUNT(*)
FROM user_table
WHERE user_id=#{userId}

SELECT pw
FROM user_table
WHERE user_id=#{userId}

SELECT profile_image
FROM user_table
WHERE user_id=#{userId}

mapper.update:
UPDATE user_table
SET enable = TRUE
WHERE user_id=#{userId}

UPDATE user_table
SET enable = FALSE
WHERE user_id=#{userId}
